import React, { useState, useEffect } from "react";
import BottomNav from "./components/BottomNav";
import GameSlider from "./components/GameSlider";
import GameFilters from "./components/GameFilters";
import TournamentSlider from "./components/TournamentSlider";
import Footer from "./components/Footer";
import RegisterModal from "./components/RegisterModal";
import LoginModal from "./components/LoginModal";
import WalletModal from "./components/WalletModal";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "./firebase";

function App() {
  const [showRegister, setShowRegister] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [showWallet, setShowWallet] = useState(false);
  const [user, setUser] = useState(null);

  // –ü–æ–¥–ø–∏—Å–∫–∞ –Ω–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  return (
    <div className="bg-black text-white min-h-screen pb-24">
      <header className="flex justify-between items-center px-4 py-3">
        <div className="text-lg font-bold">OYUUN GAMES</div>
        {user ? (
          <div className="flex items-center space-x-2">
            <div className="bg-gray-800 px-3 py-1 rounded text-sm font-semibold text-lime-400">
              + 0.00 ‚ÇΩ
            </div>
            <button className="bg-gray-800 px-3 py-1 rounded text-white font-semibold">
              üë§ –ü—Ä–æ—Ñ–∏–ª—å
            </button>
          </div>
        ) : (
          <div className="space-x-2">
            <button
              className="bg-gray-800 text-white px-3 py-1 rounded"
              onClick={() => setShowLogin(true)}
            >
              –í—Ö–æ–¥
            </button>
            <button
              className="bg-lime-400 text-black px-3 py-1 rounded font-bold"
              onClick={() => setShowRegister(true)}
            >
              –†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è
            </button>
          </div>
        )}
      </header>

      <section className="px-4">
        <h1 className="text-3xl font-bold mb-2">–ò–≥—Ä—ã –î—ã–≥—ã–Ω–∞</h1>
        <div className="bg-yellow-900 text-white p-4 rounded-lg text-center mb-4">
          <div className="text-4xl font-bold">50%</div>
          <div className="text-md">–æ—Ç –¥–æ—Ö–æ–¥–∞ –Ω–∞ –ø—Ä–∏–∑—ã</div>
        </div>
      </section>

      <GameFilters />

      <section className="px-4 mt-4">
        <h2 className="text-xl font-semibold mb-2">–°–ª–æ—Ç—ã</h2>
        <GameSlider />
      </section>

      <section className="px-4 mt-6">
        <h2 className="text-xl font-semibold mb-2">Live-–∏–≥—Ä—ã</h2>
        <GameSlider />
      </section>

      <section className="px-4 mt-6">
        <h2 className="text-xl font-semibold mb-2">–ë–ª–∏–∂–∞–π—à–∏–µ —Ç—É—Ä–Ω–∏—Ä—ã</h2>
        <TournamentSlider />
      </section>

      <Footer />
      <BottomNav />

      {/* –º–æ–¥–∞–ª–∫–∏ */}
      <RegisterModal
        isOpen={showRegister}
        onClose={() => setShowRegister(false)}
        onSuccess={() => {
          setShowRegister(false);
          setShowWallet(true);
        }}
      />
      <LoginModal isOpen={showLogin} onClose={() => setShowLogin(false)} />
      <WalletModal isOpen={showWallet} onClose={() => setShowWallet(false)} />
    </div>
  );
}

export default App;
