import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

const WalletModal = ({ isOpen, onClose, onDeposit }) => {
  const [amount, setAmount] = useState(0);
  const [method, setMethod] = useState("");

  const handleDeposit = () => {
    if (!amount || !method) return alert("Укажите сумму и способ оплаты");
    onDeposit(Number(amount), method);
  };

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          className="fixed inset-0 z-50 flex items-end justify-center bg-black bg-opacity-50"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          onClick={onClose}
        >
          <motion.div
            className="bg-zinc-900 rounded-t-2xl w-full max-w-md p-6 pt-4 text-white overflow-y-auto max-h-[90vh]"
            initial={{ y: "100%" }}
            animate={{ y: 0 }}
            exit={{ y: "100%" }}
            transition={{ type: "spring", stiffness: 300, damping: 30 }}
            onClick={(e) => e.stopPropagation()}
          >
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold">Кошелек</h2>
              <button onClick={onClose} className="text-white text-xl">✕</button>
            </div>

            <div className="flex space-x-4 mb-4">
              <button className="px-3 py-1 bg-lime-400 text-black font-bold rounded">Депозит</button>
              <button className="px-3 py-1 bg-zinc-800 rounded">Вывод</button>
              <button className="px-3 py-1 bg-zinc-800 rounded">История</button>
            </div>

            <div className="bg-zinc-800 p-4 rounded-lg text-center mb-4">
              <div className="text-sm font-semibold mb-1">До 200% и +200 FS на первый депозит</div>
              <div className="text-xs text-gray-400">До завершения: 18:46:29</div>
            </div>

            <div className="text-sm mb-2 text-gray-400">Регион оплаты</div>
            <div className="flex items-center mb-4">
              <img src="https://flagcdn.com/w40/ru.png" alt="russia" className="w-6 h-4 rounded mr-2" />
              <span>RUB</span>
            </div>

            <div className="mb-4">
              <label className="text-sm block mb-1">Сумма пополнения</label>
              <input
                type="number"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                className="w-full p-2 rounded bg-zinc-800 text-white mb-4"
              />
            </div>

            <div className="mb-4">
              <h3 className="text-md font-semibold mb-2">Банковский платеж</h3>
              <div className="grid grid-cols-2 gap-3">
                {['SberPay', 'СБП', 'Карта', 'Другой'].map((m) => (
                  <button
                    key={m}
                    onClick={() => setMethod(m)}
                    className={`p-3 rounded ${method === m ? 'bg-lime-400 text-black font-bold' : 'bg-zinc-800'}`}
                  >
                    {m}
                  </button>
                ))}
              </div>
            </div>

            <div className="mb-6">
              <h3 className="text-md font-semibold mb-2">Криптовалюта</h3>
              <div className="grid grid-cols-2 gap-3">
                {['USDT', 'TON', 'TRUMP', 'BTC'].map((m) => (
                  <button
                    key={m}
                    onClick={() => setMethod(m)}
                    className={`p-3 rounded ${method === m ? 'bg-lime-400 text-black font-bold' : 'bg-zinc-800'}`}
                  >
                    {m}
                  </button>
                ))}
              </div>
            </div>

            <button
              onClick={handleDeposit}
              className="w-full bg-lime-400 text-black font-bold py-2 rounded"
            >
              Пополнить
            </button>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default WalletModal;
